parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "news": {
      "type": "object",
      "description": "News topics with an array of related articles",
      "additionalProperties": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "title": {
              "type": "string",
              "description": "Title of the article selected from RSS feeds"
            },
            "link": {
              "type": "string",
              "format": "uri",
              "description": "Link to the article selected from RSS feeds"
            },
            "summary": {
              "type": "string",
              "description": "Summary of the article selected from RSS feeds"
            },
            "content": {
              "type": "string",
              "description": "Content of the article selected from RSS feeds"
            }
          },
          "required": ["title", "link", "summary", "content"]
        }
      }
    }
  },
  "required": ["news"]
}
